package pkg1;

public class example_code {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int condition = 0;
		int sum = 0;
		
		while(condition<=10) {
			sum = sum + condition;
			
			condition++;
			
			System.out.println("Sum value: " + sum);
			System.out.println("Condition value: " + condition);
		}

	}

}
